package uk.sky.cardealer.service;

import com.mongodb.DBObject;
import com.mongodb.client.gridfs.model.GridFSFile;
import org.bson.BsonString;
import org.bson.Document;
import org.bson.types.ObjectId;
import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.TestInstance;
import org.junit.jupiter.api.extension.ExtendWith;
import org.mockito.InjectMocks;
import org.mockito.Mock;
import org.mockito.junit.jupiter.MockitoExtension;
import org.springframework.boot.test.autoconfigure.data.mongo.DataMongoTest;
import org.springframework.boot.test.autoconfigure.web.servlet.AutoConfigureMockMvc;
import org.springframework.data.mongodb.gridfs.GridFsOperations;
import org.springframework.data.mongodb.gridfs.GridFsResource;
import org.springframework.data.mongodb.gridfs.GridFsTemplate;
import org.springframework.http.MediaType;
import org.springframework.mock.web.MockMultipartFile;
import org.springframework.test.context.ContextConfiguration;
import uk.sky.cardealer.CardealerApplication;
import uk.sky.cardealer.model.File;

import java.io.IOException;
import java.io.InputStream;
import java.util.Date;

import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.mockito.ArgumentMatchers.any;
import static org.mockito.ArgumentMatchers.anyString;
import static org.mockito.Mockito.when;

@AutoConfigureMockMvc
@ExtendWith(MockitoExtension.class)
@DataMongoTest
@ContextConfiguration(classes = CardealerApplication.class)
@TestInstance(TestInstance.Lifecycle.PER_CLASS)
public class FileServiceTest {

    @Mock
    private GridFsTemplate gridFsTemplate;

    @Mock
    private GridFsOperations gridFsOperations;

    @InjectMocks
    FileService fileService;

    private final ObjectId id = new ObjectId();

    @Test
    public void testImageUpload() throws IOException {
        when(gridFsTemplate.store(any(InputStream.class), anyString(), anyString(), any(DBObject.class))).thenReturn(id);

        MockMultipartFile fileToUpload = new MockMultipartFile(
                "file",
                "image.jpg",
                MediaType.IMAGE_JPEG_VALUE,
                "".getBytes()
        );

        Object file = fileService.uploadImage(fileToUpload, "newName");

        assertEquals(file.toString(), id.toString());
    }

    @Test
    public void testImageDownload() throws IOException {
        File originalFile = new File();
        originalFile.setFilename("newName");
        GridFSFile gridFSFile = new GridFSFile(new BsonString("1235"), "newName", 12, 12, new Date(), new Document().append("fileSize", "1244").append("_contentType", "jpeg"));
        when(gridFsTemplate.findOne(any())).thenReturn(gridFSFile);
        when(gridFsOperations.getResource(any(GridFSFile.class))).thenReturn(new GridFsResource(gridFSFile));
        File file = fileService.downloadImage("1235");

        assertEquals(originalFile.getFilename(), file.getFilename());
    }
}
